import React from "react";
import CartItem from "./CartItem";
import Cart from "./Cart";
import Navbar from "./Navbar";

class App extends React.Component {
  // added constructor for states
	constructor() {
		super();
		this.state = {
			products: [
				{
					price: 1029,
					title: 'Keypad Phone',
					quantity: 1,
					img: 'data:image/jpeg;base64,',
					id: 1
				},
				{
					price: 9119,
					title: 'SmartPhone',
					quantity: 10,
					img: 'data:image/jpeg;base64,',
					id: 2
				},
				{
					price: 100029,
					title: 'Laptop',
					quantity: 1,
					img: 'data:image/jpeg;base64,',
					id: 3
				}
			]
		};

	}

	handleIncreaseQuantity = (product) => {
		console.log("increase quantity of ", product)
		const { products} = this.state;
		const index = products.indexOf(product);

		products[index].quantity += 1;

		this.setState({
			products
		})
	}

	handleDecreaseQuantity = (product) => {
		console.log("decrease quantity of ", product)
		const { products } = this.state;
		const index = products.indexOf(product);

		if (products[index].quantity === 0){
			return ;
		}
		products[index].quantity -= 1

		this.setState({
			products
		})
	}

	handleDeleteProduct = (id) => {
		const { products } = this.state;

		const items = products.filter((item) => item.id !== id);

		this.setState({
			products: items
		})
	}

  getCartCount = () => {
    const {products} = this.state;

    let count = 0;
    products.forEach((product) => {
      count += product.quantity
    })

    return count;
  }

  getCartTotal = () => {
    const {products} = this.state;

    let cartTotal = 0;

    products.map((product) => {
      cartTotal = cartTotal + product.quantity * product.price
    })

    return cartTotal;
  }
  render () {
    const { products } = this.state;
    return (
      <div className="App">
        < Navbar  count= {this.getCartCount()}/>
        < Cart
          products = { products }
          onIncreaseQuantity = {this.handleIncreaseQuantity}
          onDecreaseQuantity = {this.handleDecreaseQuantity}
          onDeleteProduct = {this.handleDeleteProduct}
          />
          <div style={ {padding: 10, fontSize: 20,fontFamily: 'fantasy'}}> Total: {this.getCartTotal()} </div>
      </div>
    );
    }

}



export default App;
